//@Auto blink and saccades
//defined: component, node, scene, globals

var blinkIdle = 0.5 + Math.random()*6;
var blinkDur = Math.random()*0.5 + 0.15;
var prevTime = 0;

var gazeIdle = 0.5 + Math.random()*6;
var gazeDur = Math.random() + 0.5;
var prevTime2 = 0;

this.onStart = function()
{
}

this.onUpdate = function(dt)
{
  prevTime +=dt;

  if (prevTime>blinkIdle){
    // Blink
    LS.Globals.blink({end: blinkDur});

    // Saccade
    var opts = ["RIGHT", "LEFT", "DOWN","DOWNRIGHT", "DOWNLEFT"];
    var val = opts[Math.floor(Math.random()*opts.length)];

    LS.Globals.gazeShift({
      start: blinkDur*0.5,
      end: blinkDur*0.5 + Math.random()*0.1+0.1,
      target: "USER",
      influence: "EYES",
      offsetDirection: val,
      offsetAngle: Math.random()*5 + 2,
      dynamic: Math.round(Math.random())
    });


    prevTime = blinkDur;
    blinkIdle = blinkDur + 0.5 + Math.random()*6;
		blinkDur = Math.random()*0.5 + 0.15;

  }




  prevTime2 +=dt;

  if (prevTime2>gazeIdle){

    // Saccade
    var opts = ["RIGHT", "LEFT", "DOWN","DOWNRIGHT", "DOWNLEFT"];
    var val = opts[Math.floor(Math.random()*opts.length)];

    LS.Globals.gazeShift({
      start: 0,
      end: Math.random()*0.1+0.1,
      target: "USER",
      influence: "EYES",
      offsetDirection: val,
      offsetAngle: Math.random()*5 + 2,
      dynamic: Math.round(Math.random())
    });


    prevTime2 = gazeDur;
    gazeIdle = gazeDur + 0.5 + Math.random()*6;
		gazeDur = Math.random()*0.5 + 0.5;

  }

	node.scene.refresh();
}

